import React, { Component } from  'react'
import './index.css'
export default class Item extends Component {

  render() {
    const {name,done} = this.props
    return (
      <li>
          <label>
            <input type="checkbox" defalutChecked={done}/>
            <span>{name}</span>
          </label>
          <button  className='btn btn-danger' style={{display: done ? 'block' : 'none'}}>删除</button>
        </li>
    )
  }
}
