import React, { Component } from  'react'
import './index.css'
export default class Item extends Component {

  state = {mouse:false}

  handleMouse = (flag) => {
    // 鼠标移入移出的回调
    return ()=>{ 
      this.setState({mouse:flag})
    }
  }

  // 勾选、取消勾选某一项todo的回调
  handleCheck = (id)=>{
    // 复选框
    return (e)=>{
      // 接收list传递过来的参数，然后传递这个复选框的参数给  从App传递给LIst再传递到Item的 changTodo这个函数
      this.props.changeTodo(id,e.target.checked)
    }
  }

  // 删除一个todo的回调
  handleDelete = (id)=>{
    if(window.confirm('确定删除吗')) {
      // 调用从App.js传递过来的函数,把当前id传进去
      this.props.deleteTodo(id)
    }
  }

  render() {
    const { id,name,done, } = this.props
    const {mouse} = this.state
    return (
      <li className={mouse ? 'dsp' : ''} onMouseEnter={this.handleMouse(true)} onMouseLeave={this.handleMouse(false)}>
          <label>
            <input type="checkbox" checked={done} onChange={this.handleCheck(id)}/>
            <span>{name}</span>
          </label>
          <button onClick={() => this.handleDelete(id)} className='btn btn-danger' style={{display:mouse ? 'block':'none'}}>删除</button>
        </li>
    )
  }
}
