import React, { Component } from 'react'
import PropTypes from 'prop-types'
//nanoid是一个函数，每次调用都会生成随机的字符串，字符串是全球唯一的
import {nanoid} from 'nanoid'
import './index.css'
export default class Header extends Component {

  handleClick = ()=> {
    // const {ivalue} = this
    if(ivalue.value.trim() === '') {
      alert('不能为空')
      return
    }
    this.props.addTodo(ivalue.value)
  }


  // 输入框回车键回调
  handleKeyup = (e)=> {
    const {keyCode,target} = e
    if(keyCode !== 13) return
    if(target.value.trim() === '') {
      alert('输入不能为空')
      return
    }
    const newList = {
      id:nanoid(),name:target.value,done:false
    }
    this.props.addTodo(newList)
    target.value = ''
  }


  render() {
    return (
      <div className='todo-header'>
          <input type="text" placeholder='请输入你的任务名称，按回车确认'  onKeyUp={this.handleKeyup} ref={c => this.ivalue = c}></input>
          <button onClick={this.handleClick}>添加</button>
        </div>
    )
  }
}
