import React, { Component } from 'react'
//nanoid是一个函数，每次调用都会生成随机的字符串，字符串是全球唯一的
import {nanoid} from 'nanoid'
import './index.css'
export default class Header extends Component {

  state = {
    inputValue: ''
  }

  handleKeyUp = (event) => {
    // 结构赋值获取
    const {keyCode,target} = event
    if (keyCode !== 13) return
    this.state.inputValue = target.value.toString();
    // 准备好todo对象
    const todoObj = {
      id:nanoid(),
      name:target.value,
      done:false}
    // 将todoObj传入App组件
    this.props.addTodo(todoObj)
  }
  render() {
    return (
      <div className='todo-header'>
          <input type="text" placeholder='请输入你的任务名称，按回车确认' onKeyUp={this.handleKeyUp}></input>
        </div>
    )
  }
}
